

public class Program2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="RajaRamMohanRoy";
		System.out.println("The main string is:"+s1);
		boolean res1=s1.contains("Ram");
		System.out.println("contains result:"+res1);
		boolean res2=s1.contains("Sham");
		System.out.println("contains result:"+res2);
		char res3=s1.charAt(4);
		System.out.println("charAt result:"+res3);
		char res4=s1.charAt(14);
		System.out.println("charAt result:"+res4);
		int res5=s1.length();
		System.out.println("length result:"+res5);
		int res6=s1.indexOf('R');
		System.out.println("indexOf result:"+res6);
		int res7=s1.indexOf('m');
		System.out.println("indexOf result:"+res7);
		
		

	}

}
